const data = $input.all();
const today = new Date();
today.setHours(0, 0, 0, 0);
const filteredData = data.filter(item => {
  if (!item.json.appointment_followup || !Array.isArray(item.json.appointment_followup)) {
    return false;
  }
  return item.json.appointment_followup.some(followup => {
    if (followup.status !== "not_confirmed") {
      return false;
    }
    const followupDate = new Date(followup.followup_date);
    followupDate.setHours(0, 0, 0, 0);
    const diffDays = Math.ceil((followupDate - today) / (1000 * 60 * 60 * 24));
    return (!followup.isreminder1sent && diffDays === 2) || 
           (!followup.isreminder2sent && diffDays === 1);
  });
});
const temp = filteredData.map(item => {
  // Find the matching followup
  const matchedFollowup = item.json.appointment_followup.find(followup => {
    if (followup.status !== "not_confirmed") return false;
    const followupDate = new Date(followup.followup_date);
    followupDate.setHours(0, 0, 0, 0);
    const diffDays = Math.ceil((followupDate - today) / (1000 * 60 * 60 * 24));
    return (!followup.isreminder1sent && diffDays === 2) || 
           (!followup.isreminder2sent && diffDays === 1);
  });
  // Determine if this is reminder 1 or 2
  const followupDate = new Date(matchedFollowup.followup_date);
  followupDate.setHours(0, 0, 0, 0);
  const diffDays = Math.ceil((followupDate - today) / (1000 * 60 * 60 * 24));
  const isReminder1 = diffDays === 2;
  const reminderType = isReminder1 ? "" : "";
  const daysText = isReminder1 ? "2 days" : "tomorrow";

  // Use the original date string from the data
  const formattedDate = matchedFollowup.followup_date;

  // Generate message based on reason
  const reason = matchedFollowup.reason || "follow-up appointment";

  let message = `Hello! This is your reminder about your ${reason} scheduled for ${formattedDate} (${daysText} from now). `;

  if (isReminder1) {
    message += `Would you like to book an appointment?`;
  } else {
    message += `Would you like to book an appointment?`;
  }
  // Create updated appointment_followup array with the reminder flag set
  const updatedFollowups = item.json.appointment_followup.map(followup => {
    // If this is the matched followup, update the appropriate reminder flag
    if (followup.followup_date === matchedFollowup.followup_date && 
        followup.reason === matchedFollowup.reason) {
      return {
        ...followup,
        isreminder1sent: isReminder1 ? true : followup.isreminder1sent,
        isreminder2sent: isReminder1 ? followup.isreminder2sent : true
      };
    }
    return followup;
  });

  // Create new message object
  const newMessage = {
    role: "follow_appointment",
    message: message,
    timestamp: new Date().toISOString()
  };

  // Add new message to existing messages array
  const updatedMessages = [
    ...(item.json.messages || []),
    newMessage
  ];

  // Create the complete updated data object
  const updatedData = {
    appointment_followup: updatedFollowups,
    messages: updatedMessages
  };

  return {
    contact: item.json.patient_contact,
    message: message,
    followup_date: matchedFollowup.followup_date,
    reason: matchedFollowup.reason,
    reminder_type: isReminder1 ? 1 : 2,
    updated_followups: updatedData
  };
});
return temp;
